function getRSAKeys(){
	global $smcFunc;
	header('Content-Type:application/json');
	$ret = array();
	for($i = 0;$i < (int)$_GET['num'];$i++){

		$res = openssl_pkey_new();
		openssl_pkey_export($res,$privKey);
		$pubKey = openssl_pkey_get_details($res);
		$pubKey = $pubKey['key'];

		$smcFunc['db_insert']('insert',
			'{db_prefix}rsa_keys',
			array(
				'privKey' => 'string', 'pubKey' => 'string'
			),
			array($privKey,$pubKey),
			array('privKey','pubKey')
		);
		$ret[] = array(
			'i' => $smcFunc['db_insert_id']('{db_prefix}rsa_keys','key_id'),
			'key' => $pubKey
		);
	}

	echo json_encode($ret);
	exit;
}

function getRSAValue($crypt_unparsed){
	global $smcFunc;
	$smcFunc['db_query']('','DELETE FROM {db_prefix}rsa_keys WHERE ts < (NOW() - INTERVAL 1 MINUTE)');
	$crypt = array();
	$fetchKeys = array();
	foreach($crypt_unparsed as $c){
		$c = explode('|',$c,2);
		if(sizeof($c) != 2 || in_array((int)$c[0],$fetchKeys)){
			$crypt[] = array(
				'i' => -1,
				'val' => ''
			);
		}else{
			$crypt[] = array(
				'i' => (int)$c[0],
				'val' => $c[1]
			);
			$fetchKeys[] = (int)$c[0];
		}
	}
	$dbKeys = array();
	$request = $smcFunc['db_query']('','SELECT key_id,privKey FROM {db_prefix}rsa_keys WHERE key_id IN ('.implode(',',$fetchKeys).')');
	while($res = $smcFunc['db_fetch_assoc']($request)){
		$dbKeys[(int)$res['key_id']] = $res['privKey'];
	}
	$smcFunc['db_free_result']($request);
	$smcFunc['db_query']('','DELETE FROM {db_prefix}rsa_keys WHERE key_id IN ('.implode(',',$fetchKeys).')');

	$cryptRet = array();
	foreach($crypt as $c){
		if($c['i'] == -1 || !isset($dbKeys[$c['i']])){
			$cryptRet[] = '';
		}else{
			if(openssl_private_decrypt(base64_decode($c['val']),$plain,openssl_get_privatekey($dbKeys[$c['i']]))){
				$cryptRet[] = $plain;
			}else{
				$cryptRet[] = '';
			}
		}
	}
	return $cryptRet;
}

